const login = new Vue({
  el: '#login',
  data: {
    email: null,
    password: null,
    success: false,
    error: false,
    loading: false
  },
  methods: {
    submit: function() {
      this.loading = true;
      const params = { email: this.email, password: this.password }
      setTimeout(() => {
        axios.post('/login', params)
          .then(res => window.location = "../")
          .catch(err => {
            console.log(err.response)
            this.loading = false;
          })
      }, 1200)
    }
  }
})