const login = new Vue({
	el: '#login',
	data: {
		first: null,
		email: null,
		password: null,
		success: false,
		error: false,
		loading: false
	},
	methods: {
		submit: function() {
			this.loading = true;
			const params = {
				first: this.first,
				email: this.email,
				password: this.password
			}
			setTimeout(() => {
				axios.post('/signup', params)
					.then(res => {
						this.loading = false
						window.location = "../";
					})
					.catch(err => {
						console.log(err.response)
						this.loading = false;
					})
			}, 1200)
		}
	}
})